import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import styled from 'styled-components';
import { 
  Mic, 
  Bot, 
  Activity, 
  Zap, 
  Shield, 
  Clock,
  MessageSquare,
  Play,
  FileText,
  Calculator,
  Palette,
  FolderOpen
} from 'lucide-react';
import toast from 'react-hot-toast';

const DashboardContainer = styled(motion.div)`
  max-width: 1200px;
  margin: 0 auto;
`;

const Header = styled(motion.div)`
  margin-bottom: 3rem;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: 700;
  background: linear-gradient(135deg, #FF6B35, #E55A2B);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  font-size: 1.1rem;
  color: #BDC3C7;
  margin-bottom: 2rem;
`;

const StatsGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin-bottom: 3rem;
`;

const StatCard = styled(motion.div)`
  background: rgba(44, 62, 80, 0.8);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 1.5rem;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    background: rgba(44, 62, 80, 1);
    border-color: rgba(255, 107, 53, 0.3);
    transform: translateY(-4px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
  }
`;

const StatHeader = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 1rem;
`;

const StatIcon = styled.div`
  width: 48px;
  height: 48px;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 1rem;
  background: ${props => props.color};
  color: white;
`;

const StatInfo = styled.div`
  flex: 1;
`;

const StatLabel = styled.h3`
  font-size: 0.875rem;
  color: #BDC3C7;
  font-weight: 500;
  margin-bottom: 0.25rem;
`;

const StatValue = styled.div`
  font-size: 1.5rem;
  font-weight: 700;
  color: #FFFFFF;
`;

const ChatSection = styled(motion.div)`
  background: rgba(44, 62, 80, 0.8);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 2rem;
  margin-bottom: 3rem;
  backdrop-filter: blur(10px);
`;

const ChatHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1.5rem;
`;

const ChatTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #FFFFFF;
  display: flex;
  align-items: center;

  svg {
    margin-right: 0.75rem;
    color: #FF6B35;
  }
`;

const ChatContainer = styled.div`
  height: 300px;
  overflow-y: auto;
  padding: 1rem;
  background: rgba(52, 73, 94, 0.5);
  border-radius: 12px;
  border: 1px solid rgba(255, 255, 255, 0.05);
`;

const Message = styled(motion.div)`
  margin-bottom: 1rem;
  padding: 0.75rem 1rem;
  border-radius: 8px;
  background: ${props => props.isUser ? 'rgba(255, 107, 53, 0.2)' : 'rgba(52, 73, 94, 0.8)'};
  border-left: 3px solid ${props => props.isUser ? '#FF6B35' : '#3498DB'};
`;

const MessageText = styled.p`
  color: #FFFFFF;
  font-size: 0.875rem;
  line-height: 1.4;
`;

const QuickActions = styled(motion.div)`
  margin-bottom: 3rem;
`;

const ActionsTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 1.5rem;
  display: flex;
  align-items: center;

  svg {
    margin-right: 0.75rem;
    color: #FF6B35;
  }
`;

const ActionsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
`;

const ActionButton = styled(motion.button)`
  background: linear-gradient(135deg, #2C3E50, #34495E);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 1.5rem;
  color: #FFFFFF;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  transition: all 0.3s ease;

  &:hover {
    background: linear-gradient(135deg, #34495E, #2C3E50);
    border-color: rgba(255, 107, 53, 0.3);
    transform: translateY(-2px);
  }

  svg {
    width: 32px;
    height: 32px;
    margin-bottom: 0.75rem;
    color: #FF6B35;
  }
`;

const VoiceButton = styled(motion.button)`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: linear-gradient(135deg, #FF6B35, #E55A2B);
  border: none;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;
  box-shadow: 0 8px 25px rgba(255, 107, 53, 0.4);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;

  &:hover {
    background: linear-gradient(135deg, #E55A2B, #D44A1B);
    transform: scale(1.1);
  }
`;

const quickActions = [
  { icon: FileText, label: 'Bloc-notes', action: 'notepad' },
  { icon: Calculator, label: 'Calculatrice', action: 'calc' },
  { icon: Palette, label: 'Paint', action: 'mspaint' },
  { icon: FolderOpen, label: 'Explorateur', action: 'explorer' },
];

const Dashboard = ({ socket }) => {
  const [messages, setMessages] = useState([
    { id: 1, text: 'Bienvenue dans votre assistant vocal intelligent !', isUser: false, timestamp: new Date() },
  ]);
  const [isListening, setIsListening] = useState(false);
  const [stats, setStats] = useState({
    commandsExecuted: 0,
    voiceInteractions: 0,
    systemProtected: true,
    uptime: '00:00:00'
  });

  useEffect(() => {
    if (socket) {
      socket.on('voice_response', (data) => {
        setMessages(prev => [...prev, {
          id: Date.now(),
          text: data.response,
          isUser: false,
          timestamp: new Date()
        }]);
        toast.success('Commande exécutée avec succès !');
      });

      socket.on('command_executed', (data) => {
        setStats(prev => ({
          ...prev,
          commandsExecuted: prev.commandsExecuted + 1
        }));
      });
    }
  }, [socket]);

  const handleQuickAction = (action) => {
    if (socket) {
      socket.emit('quick_action', { action });
      toast.success(`Action ${action} lancée !`);
    }
  };

  const handleVoiceCommand = () => {
    setIsListening(!isListening);
    if (socket) {
      socket.emit('voice_command', { command: 'start_listening' });
      toast.success('Écoute activée...');
    }
  };

  return (
    <DashboardContainer
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <Header>
        <Title>Tableau de Bord</Title>
        <Subtitle>Surveillez et contrôlez votre système avec la voix</Subtitle>
      </Header>

      <StatsGrid
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.2 }}
      >
        <StatCard
          whileHover={{ scale: 1.02 }}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
        >
          <StatHeader>
            <StatIcon color="#27AE60">
              <Activity size={24} />
            </StatIcon>
            <StatInfo>
              <StatLabel>Commandes Exécutées</StatLabel>
              <StatValue>{stats.commandsExecuted}</StatValue>
            </StatInfo>
          </StatHeader>
        </StatCard>

        <StatCard
          whileHover={{ scale: 1.02 }}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
        >
          <StatHeader>
            <StatIcon color="#3498DB">
              <Mic size={24} />
            </StatIcon>
            <StatInfo>
              <StatLabel>Interactions Vocales</StatLabel>
              <StatValue>{stats.voiceInteractions}</StatValue>
            </StatInfo>
          </StatHeader>
        </StatCard>

        <StatCard
          whileHover={{ scale: 1.02 }}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
        >
          <StatHeader>
            <StatIcon color={stats.systemProtected ? "#27AE60" : "#E74C3C"}>
              <Shield size={24} />
            </StatIcon>
            <StatInfo>
              <StatLabel>Statut Système</StatLabel>
              <StatValue>{stats.systemProtected ? 'Protégé' : 'Vulnérable'}</StatValue>
            </StatInfo>
          </StatHeader>
        </StatCard>

        <StatCard
          whileHover={{ scale: 1.02 }}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6 }}
        >
          <StatHeader>
            <StatIcon color="#F39C12">
              <Clock size={24} />
            </StatIcon>
            <StatInfo>
              <StatLabel>Temps de Fonctionnement</StatLabel>
              <StatValue>{stats.uptime}</StatValue>
            </StatInfo>
          </StatHeader>
        </StatCard>
      </StatsGrid>

      <ChatSection
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.7 }}
      >
        <ChatHeader>
          <ChatTitle>
            <MessageSquare />
            Historique des Interactions
          </ChatTitle>
        </ChatHeader>
        <ChatContainer>
          <AnimatePresence>
            {messages.map((message) => (
              <Message
                key={message.id}
                isUser={message.isUser}
                initial={{ opacity: 0, x: message.isUser ? 20 : -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: message.isUser ? -20 : 20 }}
                transition={{ duration: 0.3 }}
              >
                <MessageText>{message.text}</MessageText>
              </Message>
            ))}
          </AnimatePresence>
        </ChatContainer>
      </ChatSection>

      <QuickActions
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.8 }}
      >
        <ActionsTitle>
          <Zap />
          Actions Rapides
        </ActionsTitle>
        <ActionsGrid>
          {quickActions.map((action, index) => (
            <ActionButton
              key={action.action}
              onClick={() => handleQuickAction(action.action)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.9 + index * 0.1 }}
            >
              <action.icon />
              {action.label}
            </ActionButton>
          ))}
        </ActionsGrid>
      </QuickActions>

      <VoiceButton
        onClick={handleVoiceCommand}
        animate={isListening ? { scale: [1, 1.2, 1] } : {}}
        transition={{ duration: 0.5, repeat: isListening ? Infinity : 0 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        <Mic />
      </VoiceButton>
    </DashboardContainer>
  );
};

export default Dashboard;
